#   {{{2
import time
import math
import copy
from collections import deque
from typing import List, Tuple, Optional

class Solution:

    #   runtime: beats 18%
    def nearestExit(self, maze: List[List[str]], entrance: Tuple[int]) -> int:

        def is_exit(position: Tuple[int,int]) -> bool:
            if position[0] == entrance[0] and position[1] == entrance[1]:
                return False
            if maze[position[0]][position[1]] == '+':
                return False
            if position[0] == 0 or position[0] == len(maze)-1:
                return True
            if position[1] == 0 or position[1] == len(maze[0])-1:
                return True
            return False

        def get_options(position: Tuple[int,int]) -> List[Tuple[int,int]]:
            result = []
            for delta_i in range(-1,2):
                candidate = (position[0]+delta_i, position[1])
                if candidate[0] == position[0] and candidate[1] == position[1]:
                    continue
                if candidate[0] < 0 or candidate[0] >= len(maze):
                    continue
                if candidate in visited:
                    continue
                if maze[candidate[0]][candidate[1]] == '.':
                    result.append(candidate)
            for delta_j in range(-1,2):
                candidate = (position[0], position[1]+delta_j)
                if candidate[0] == position[0] and candidate[1] == position[1]:
                    continue
                if candidate[1] < 0 or candidate[1] >= len(maze[0]):
                    continue
                if candidate in visited:
                    continue
                if maze[candidate[0]][candidate[1]] == '.':
                    result.append(candidate)
            return result

        visited = set()
        queue = deque()
        queue.append( (tuple(entrance), 0) )

        while len(queue) > 0:
            current_position, current_distance = queue.popleft()
            visited.add(current_position)
            if is_exit(current_position):
                return current_distance
            for next_position in get_options(current_position):
                queue.append( (next_position,current_distance+1) )
                visited.add(next_position)

        return -1


    #   runtime: beats 85%
    def nearestExit_ans(self, maze: List[List[str]], entrance: List[int]) -> int:
        rows, cols = len(maze), len(maze[0])
        dirs = ((1, 0), (-1, 0), (0, 1), (0, -1))
        start_row, start_col = entrance
        maze[start_row][start_col] = "+"
        queue = deque()
        queue.append([start_row, start_col, 0])
        while queue:
            curr_row, curr_col, curr_distance = queue.popleft()
            for d in dirs:
                next_row = curr_row + d[0]
                next_col = curr_col + d[1]
                if 0 <= next_row < rows and 0 <= next_col < cols and maze[next_row][next_col] == ".":
                    if 0 == next_row or next_row == rows - 1 or 0 == next_col or next_col == cols - 1:
                        return curr_distance + 1
                    maze[next_row][next_col] = "+"
                    queue.append([next_row, next_col, curr_distance + 1])
        return -1


s = Solution()
test_functions = [ s.nearestExit, s.nearestExit_ans, ]

